import Container from "react-bootstrap/Container";
import Navbar from "react-bootstrap/Navbar";
import logoLu from "../assets/Logo_Principal.jpg";
import Row from "react-bootstrap/Row";
import InputGroup from "react-bootstrap/InputGroup";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

function Header() {
  return (
    <Navbar
      bg="light"
      data-bs-theme="light"
      style={{
        width: "100%",
        height: "300px",
        backgroundColor: "white",
      }}
    >
      <Container
        style={{
          display: "flex",
          flexFlow: "row",
          justifyContent: "flex-start",
          alignContent: "center",
        }}
      >
        <Navbar.Brand href="#home">
          <Logo />
        </Navbar.Brand>
        <InteractiveBar />
      </Container>
    </Navbar>
  );
}

const Logo = () => {
  return (
    <a href="/">
      <img
        src={logoLu}
        alt="Logo Lu"
        style={{ width: "250px", height: "auto" }}
      />
    </a>
  );
};

const InteractiveBar = () => {
  return (
    <div style={{ width: "100%", display: "flex", flexFlow: "column" }}>
      <Row
        style={{
          marginLeft: "5em",
          display: "flex",
          flexFlow: "row",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <SearchBar />
        <RrssBar />
      </Row>
      <Buttons />
    </div>
  );
};

const SearchBar = () => {
  return (
    <Row
      style={{
        marginTop: "3em",
        display: "flex",
        flexFlow: "row",
        justifyContent: "flex-start",
      }}
    >
      <InputGroup>
        <Form.Control
          type="text"
          placeholder="Search"
          className=" mr-sm-2"
          style={{
            width: "500px",
            height: "30px",
            borderColor: "#dc1926",
            borderRadius: "50px",
          }}
        />
        <Button
          type="submit"
          style={{
            width: "150px",
            height: "30px",
            borderColor: "#dc1926",
            borderRadius: "50px",
            fontFamily: "DeVinne Txt BT-Regular",
            fontSize: "20px",
          }}
        >
          Search
        </Button>
      </InputGroup>
    </Row>
  );
};

